function setProgress()
{
	var plan_rate = ds_master.GetColumn(ds_master.row, "PLAN_RATE");
	var plan_rate_idx = 6 + parseInt(parseInt(plan_rate)/2);
	var real_rate = ds_master.GetColumn(ds_master.row, "REAL_RATE");
	var real_rate_idx = 62 + parseInt(parseInt(real_rate)/2);
	
	var cellCnt = grd_type2.GetCellCount("body");
	for(var idx=0; idx<cellCnt; idx++)
	{
		// 윗줄 [7-56]
		if(idx>6 && idx<57)
		{
			if( plan_rate_idx>idx){
				grd_type2.SetCellProp("body", idx, "bkColor", "green");
				grd_type2.SetCellProp("body", idx, "bkColor2", "green");
			}
		}
		// 아랫줄 [63-112]
		else if(idx>62 && idx<113)
		{
			if( real_rate_idx>idx){
				grd_type2.SetCellProp("body", idx, "bkColor", "orange");
				grd_type2.SetCellProp("body", idx, "bkColor2", "orange");
			}
		}
		else
		{
			continue;
		}
		
		//trace("Cell["+idx+"] :: ["+grd_type2.GetCellProp("body", idx, "text")+"]");
	}
}

