	str_arg = "arg_grid=" + quote("Tab0.tab2.Grid_main");
	str_result = Dialog("func::func_05_Pop.xml", str_arg, 350, 225, true, -1, -1);
	
	if ( (str_result <> null)  && (str_result <> "") )
