// Soft 기능 수행..
function fn_sort(obj, nCell)
{
	var strSortType = obj.GetCellProp("head",nCell,"text");
	var ds_obj = object(obj.BindDataset);
	
	// Soft 구분자 분리 ..
	strSortType = right(strSortType,1);
		
	for (var i=0;i<obj.GetCellCount("head");i++)
	{
		// Head Sort 
		obj.SetCellProp("head",i,"text",replace(replace(obj.GetCellProp("head",i,"text"),"▲"),"▼"));
	}
	
	if (strSortType == "▲")
	{
		obj.SetCellProp("head",nCell,"text",obj.GetCellProp("head",nCell,"text")+"▼");
		ds_obj.Sort(obj.GetCellProp("body",nCell,"colid")+":D");
	} else {
		obj.SetCellProp("head",nCell,"text",obj.GetCellProp("head",nCell,"text")+"▲");
		ds_obj.Sort(obj.GetCellProp("body",nCell,"colid")+":A");
	}
}


function Tab0_tab2_Grid1_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	fn_sort(obj, nCell);
}



function func_03_OnLoadCompleted(obj)
{
	DS_Sort.Copy("dataset0");
	DS_Drag.Copy("dataset0");
	DS_size2.Copy("dataset0");
	DS_fix.Copy("dataset0");	
}

function Checkbox0_OnClick(obj,strValue)
{
	if ( strValue )
		ds_list1.GroupKey =	"column0,column1,column2";
	else
		ds_list1.GroupKey =	"";	
}

function Checkbox1_OnClick(obj,strValue)
{
	if ( Checkbox1.Value == true )
	{
		ds_list1.ReverseSubSum = true;
	}
	else
	{	
		ds_list1.ReverseSubSum = false;
	}	
}
