function img_popup_OnClick(obj,nX,nY)
{
	var objForm = GetTopWindow();
	var objFormId = objForm.id;
	var objVal = objFormId;
	
	if ( length(objVal) == 0 ) return;
	
	//trace("objVal : " + objVal);
	
	var fRow = gds_menu.FindRow("FORMID", objVal);
	var str_prefix = gds_menu.GetColumn(fRow, "PREFIX");
	var str_formid = gds_menu.GetColumn(fRow, "FORMID");
	
		
	Dialog(str_prefix+"::"+str_formid, "", -1, -1, true, -1, -1);
	
}
