function func_ChartInit0()
{
	var objFusionchart = FusionChart30;
	
	objFusionchart.Clear();
	
	//objFusionchart.AddPropValue("caption", 'Monthly Sales Summary Comparison');
	//objFusionchart.AddPropValue("xAxisName", 'Month');
	//objFusionchart.AddPropValue("yAxisName", 'Sales');
	objFusionchart.AddPropValue("bgColor", 'ffffff');
	objFusionchart.AddPropValue("showborder", '0');		// 보더 조정 ..
	objFusionchart.AddPropValue("showNames", '1');
	//objFusionchart.AddPropValue("bgSWF", 'http://miplatform.co.kr/img/main/img.jpg');
	objFusionchart.AddPropValue("canvasBgAlpha", '30');
	objFusionchart.AddPropValue("animation", '1');
	objFusionchart.AddPropValue("formatNumberScale", '0');   //0 ,1, 
	objFusionchart.AddPropValue("numDivLines", "5");
	//objFusionchart.AddPropValue("numberSuffix", 'days');   //0 ,1, 
	
		
	str_color1 = replace(ds_color.GetColumn(0, "data"),"#","");
	str_color2 = replace(ds_color.GetColumn(5, "data"),"#","");
	str_color3 = replace(ds_color.GetColumn(10, "data"),"#","");
	str_color4 = replace(ds_color.GetColumn(15, "data"),"#","");
	
	objFusionchart.AddDataset("ds_chart1", "QTY1", "seriesName", 'Profit margin', "color", str_color1, 'showValues', '1'); //'dashed', '1');//, 'alpha', '50');
	objFusionchart.AddDataset("ds_chart1", "QTY2", "seriesName", 'Avg.orders', "color", str_color2, 'showValues', '1');
	objFusionchart.AddDataset("ds_chart1", "QTY3", "seriesName", 'EOH Inventory', "color", str_color3, 'showValues', '1');
	objFusionchart.AddDataset("ds_chart1", "QTY4", "seriesName", 'Order fill rate', "color", str_color4, 'showValues', '1');
	
	FusionChart30.defineStyle("animation", "myanistyle", "param", "_xScale", "start", "0", "duration", "2", "alpha", "50");
	FusionChart30.defineStyle("Shadow", "myshadowstyle", "color", "CCCCCC");
	FusionChart30.defineStyle("Bevel", "mybevelstyle", "shadowColor", "000000", "shadowAlpha", "80", "distance", "5", "angle", "45");
	FusionChart30.defineStyle("font", "myfontstyle", "face", "Verdana", "size", "14", "color", "FAA540", "bgcolor", "AFD8F8");
	
	FusionChart30.ApplyStyle("DataPlot", "mybevelstyle,myshadowstyle");
	FusionChart30.ApplyStyle("Canvas", "myanistyle");
	FusionChart30.ApplyStyle("caption", "myfontstyle,mybevelstyle");
    
    // 라벨...
	for(var i=0; i<ds_chart1.RowCount(); i++){
		str_val = ds_chart1.GetColumn(i, "SALE_AREA_DESC1");
		FusionChart30.Addcategory(str_val, "hoverText", str_val);
	}
}

