function Chart0_Display()
{
	objFusionchart = FusionChart30;
	
	
	
	// xml data 생성
	var f_xmldata = objFusionchart.MakeChartData();

	//alert(f_xmldata);
	var strDir = AliasToRealPath("%COMPONENT%");
	
	//objFusionchart.Movie = strDir + "\\chart_v3\\MSLine.swf";
	objFusionchart.Movie = strDir + "\\chart_v3\\MSBar3D.swf";
	//objFusionchart.Movie = strDir + "\\charts\\MSColumn2D.swf";
	//objFusionchart.Movie = strDir + "\\charts\\MSColumn3D.swf";
	
	//objFusionchart.Movie = strDir + "\\charts\\MSColumn3DLineDY.swf";
	
	
	objFusionchart.SetVariable('dataXML', f_xmldata);
	objFusionchart.SetVariable('chartWidth', objFusionchart.Width);	// chart size 지정
	objFusionchart.SetVariable('chartHeight', objFusionchart.Height);	// chart size 지정
	
	objFusionchart.SetVariable('debugMode', 0 );	// 0, 1  지정
	//objFusionchart.SetVariable('scaleMode', "0");	// chart size 지정
	
	objFusionchart.Stop();
//	objFusionchart.Forward();
	objFusionchart.Rewind();
	objFusionchart.Play();
}


