//max값 구하서 leftValue Setting 하기
function funcMaxValue(dsObj, chtObj, Azis)
{
	var lsOriginValue = 0;
	var lsRowCount = dsObj.RowCount();
	var lsColCount = dsObj.GetColCount();
	
	for(var j=0; j<lsRowCount; j++){
		var lsValue = dsObj.GetColumn(j, "QTY3");
		if(lsValue >= lsOriginValue){
				lsOriginValue = lsValue;
		}
	}

	var lsMaxValue = ceil((lsOriginValue+10)/10)*11;
	
	if ( Azis == "bottom" )
		chtObj.Axis.Bottom.SetMinMax(0,lsMaxValue);		//left Axis 값범위 Setting
	else
		chtObj.Axis.left.SetMinMax(0,lsMaxValue);		//left Axis 값범위 Setting
}

//max값 구하서 leftValue Setting 하기
function funcMaxValues(dsObj, chtObj, Azis)
{
	var lsOriginValue = 0;
	var lsRowCount = dsObj.RowCount();
	var lsColCount = dsObj.GetColCount();
	
	for(var j=0; j<lsRowCount; j++){
		for(var i=0; i<lsColCount; i++){
			var lsID = dsObj.GetColID(i);
			var lsValue = dsObj.GetColumn(j, lsID);
			if(lsValue >= lsOriginValue){
					lsOriginValue = lsValue;
			}
		}
	}

	var lsMaxValue = ceil((lsOriginValue+10)/10)*11;
	
	if ( Azis == "bottom" )
		chtObj.Axis.Bottom.SetMinMax(0,lsMaxValue);		//left Axis 값범위 Setting
	else
		chtObj.Axis.left.SetMinMax(0,lsMaxValue);		//left Axis 값범위 Setting
}



