function DataGrid()
{
//-------------------------------------------- 
	var i, j, nRow0, nRow1, nRow2, nRow3;
	//------------------------------------------------
	//    데이터셋의 데이터를 세트한다.
	//------------------------------------------------
	DS_GRID.FireEvent = false;
	var nGridColCnt = ds_chart1.RowCount();
	var nRowCnt = 2;
	// 데이터셋의 내용을 지운다.
	DS_GRID.Clear();
	DS_GRID.DeleteAll();
	
	//trace(">>>> SetChartGrid : 데이터셋의 컬럼정보를 만든다.------------");
	// 데이터셋의 컬럼정보를 만든다.
	DS_GRID.AddColumn("G_TITLE","String",255);
	for (i=0; i<nGridColCnt;i++)
	{							
		DS_GRID.AddColumn("COL"+i,"String",255);
	}			
	//dsprn(DS_GRID);
	
	//trace(">>>> SetChartGrid : 데이터셋에 데이터를 세트한다.-------------");
	// 데이터셋에 데이터를 세트한다.		
	// Row를 추가한다.
	nRow0 = DS_GRID.AddRow();			
	nRow1 = DS_GRID.AddRow();			
	nRow2 = DS_GRID.AddRow();
	nRow3 = DS_GRID.AddRow();

	//------------------------------------------------
	//타이틀을 세트한다.
	//------------------------------------------------
	DS_GRID.SetColumn(nRow0,"G_TITLE","Profit margin"); 
	DS_GRID.SetColumn(nRow1,"G_TITLE","Avg.orders"); 
	DS_GRID.SetColumn(nRow2,"G_TITLE","EOH Inventory"); 
	DS_GRID.SetColumn(nRow3,"G_TITLE","Order fill rate"); 

	//------------------------------------------------
	// 데이터부분을 세트한다.
	//------------------------------------------------
	var sVal1;
	var nTotal1=0,nTotal2=0,nTotal3=0;
	for (j=0; j<nGridColCnt;j++)
	{							
		// 계획량을 넣는다.	
		sVal1 = toNumber(ds_chart1.GetColumn(j, 'QTY1'));
		DS_GRID.SetColumn(nRow0,"COL"+j, sVal1 );
					
		sVal1 = toNumber(ds_chart1.GetColumn(j, 'QTY2'));
		DS_GRID.SetColumn(nRow1,"COL"+j, sVal1 );
					
		sVal1 = toNumber(ds_chart1.GetColumn(j, 'QTY3'));
		DS_GRID.SetColumn(nRow2,"COL"+j, sVal1 );
					
		sVal1 = toNumber(ds_chart1.GetColumn(j, 'QTY4'));
		DS_GRID.SetColumn(nRow3,"COL"+j, sVal1 );

	}

	//dsprn(DS_GRID);
 	//------------------------------------------------
	// 그리드의 내용을 세트한다.
	//------------------------------------------------
	
	SetGridContents(nGridColCnt);
	//GRD_GRAPH.SetCellPos(nGridColCnt+1);

}

function SetGridContents(nCount)
{
//---------------------------------------------
	var strGridContents;
	var nGbColSize = 45;
	var nTotalColSize = 58;

	strGridContents = "";
	strGridContents += "<contents>\n";
	
	//----------------------------------------------
	// 각 Column의 사이즈를 계산한다.
	//----------------------------------------------
	var nColSize=0,nLastColSize=0;
	
	//var nMaxSize = GRD_GRAPH.width - nGbColSize - 2;  // 2는 테두리선두께
	var nMaxSize = GRD_GRAPH.width - nTotalColSize - nGbColSize - 2;  // 2는 테두리선두께
	
	// 컬럼이 9개 이상인 경우 사이즈를 더 이상 줄이지 않고, 스크롤이 생기도록 처리
	nSize = nCount;
	/*
	if (nSize > 9) 
	{
		nSize = 9;
		GRD_GRAPH.height = 74 + 17;
	}
	else
		GRD_GRAPH.height = 74;
	*/

	nColSize = toInteger(nMaxSize / nSize);
	var nComputeColSize = nColSize * nSize;
	if (nComputeColSize > nMaxSize) nColSize--;
	
	nLastColSize = nMaxSize - (nColSize * (nSize-1));
	
	strGridContents += "  <columns> \n" ;
	strGridContents += "    <col fix='true' width='120'/>\n";
	for( index=0 ; index < nCount ; index++ )
	{	
		if (index == nCount-1) 
			strGridContents += "    <col width='" + nLastColSize + "'/>\n";
		else
			strGridContents += "    <col width='" + nColSize + "'/>\n";
	}

	strGridContents += "  </columns>\n";
	
	//----------------------------------------------
	//  Head 정보를 생성한다.
	//----------------------------------------------
	var sVal;
	strGridContents += "  <head>\n";
	// 구분
	strGridContents += "    <cell align='center' bkcolor='#b5cbde' col='0' display='text' text='Status'/>\n";

	for( index=0 ; index < nCount ; index++ )
	{	
		sVal = ds_chart1.GetColumn(index,"SALE_AREA_DESC1");
    //okalert("sval => " +  sVal);
		strGridContents += "    <cell align='center' bkcolor='#b5cbde' col='"+(index+1)+"' display='text' text='"+sVal+"'/>\n";
	}
	strGridContents += "  </head>\n";

	//----------------------------------------------
	//  Body 정보를 생성한다.
	//----------------------------------------------
	strGridContents += "  <body>\n";
	strGridContents += "<cell col='0' colid='G_TITLE' bkcolor='#e4e9f1' bkcolor2='#dde2ea' align='center'/>\n";
	for( index=0 ; index < nCount ; index++ )
	{
		//alert(currow);
		//strGridContents += "<cell col='" + (index+1) + "'  color='expr:iif(&#32;currow&#32;==&#32;2,&#32;iif(&#32;toNumber(Col" + (index) + ")&#32;&lt;&#32;100,&#32;&apos;red&apos;,&#32;&apos;blue&apos;&#32;)&#32;,&#32;&apos;black&apos;&#32;)' expr='iif(&#32;currow&#32;=&#32;2,&#32;numformat(round(Col" + (index) + ",1),1)&#32;+&#32;&apos;%&apos;,&#32;numformat(Col" + (index) + ")&#32;&#32;)'  align='right' display='text' />";
		strGridContents += "<cell col='" + (index+1) + "' bkcolor='#f4f7f5' bkcolor2='#edf1ee' color='expr:iif(&#32;currow&#32;==&#32;2,&#32;iif(&#32;toNumber(Col" + (index) + ")&#32;&lt;&#32;100,&#32;&apos;red&apos;,&#32;&apos;blue&apos;&#32;)&#32;,&#32;&apos;black&apos;&#32;)' expr='numformat(Col" + (index) + ")'  align='right' display='text' />";
		strGridContents += "\n";
	}		
	strGridContents += "</body>
		</contents>
	";
		
	GRD_GRAPH.Redraw = false;
	GRD_GRAPH.Contents = strGridContents;
	GRD_GRAPH.Redraw = true;
	//trace("strGridContents =[\n" + strGridContents + "\n]");
}
