	{
		KillTimer(1);
		Tab0.tab6.Grid_main.Redraw = false;
		var ds = Object(Tab0.tab6.Grid_main.binddataset);
		for ( var i = 0; i < ds.rowcount; i ++ )
		{
			Tab0.tab6.Grid_main.AutoSizeRow("Row", i, 4);
		}
		Tab0.tab6.Grid_main.Redraw = true;
		
	}	
}


