function btn_create_OnClick(obj)
{
	file.open("at");
	var str = "";
	for( var i = 0; i < ds_list.rowcount(); i++ ) {
		str = "INSERT INTO " + ed_tableName.value + " ( ";
		for( var c = 0; c < ds_list.GetColCount(); c++ ) {
			if ( c > 0 ) {
				str += " , ";
			}
			
			str += ds_list.GetColID(c);
		}
		
		str += " ) VALUES ( " ;
		for( var c = 0; c < ds_list.GetColCount(); c++ ) {
			if ( c > 0 ) {
				str += " , ";
			}
			
			str += "'" + trim(ds_list.getColumn(i, ds_list.GetColID(c))) + "'";
		}
		str += ");";
		file.write(str + "\r\n");
	}	
	file.close();
}

function btn_app_OnClick(obj)
{
		for( var i = 0; i < ds_list.rowcount(); i++ ) {
			ds_list.setColumn(i, ed_colName.value, ed_value.value );
		}
}


/******************************************************
 * ׼  Dataset load
 * @param  grdObj  import ó ׸ ü
 *  :
******************************************************/
function gfn_importExcel(grdObj) {
	Create("FileDialog", "_fileDialog_", "");
	var dialogObj = object("_fileDialog_");

	dialogObj.Type = "Open";
	dialogObj.FileName = "";
	dialogObj.Filter = "Microsoft Excel File(*.xls)|*.xlsx|";
	dialogObj.open();

	if (length(dialogObj.FileName) == 0) {
		Destroy( "_fileDialog_" );
		return;
	}

	var arg = "fa_grdName=" + quote( "tab0.tab3.grd_user");
	arg+= " fa_excelFile=" + quote( dialogObj.FilePath + "\\" + dialogObj.FileName);

	Destroy( "_fileDialog_" );
	gfn_openPopupUrl("semi_excel::ColumnMappingPopup.xml", true, arg );
}

function gfn_openPopupUrl(url, isModal, arg, w, h, x , y) {
	var leftX = x;
	var topY = y;

    if ( leftX == null || toNumber(leftX) <= 1) {
        leftX = -1;
    }

    if ( topY == null || toNumber(topY) <= 1 ) {
        topY = -1;
    }

	if ( isModal ) {
		return Dialog(url, arg, 1, 1, "TitleBar=false StatusBar=false", leftX, topY);
	} else {
		open(url, arg, 1, 1, "TitleBar=false StatusBar=false TaskBar=true", leftX, topY);
	}
}

var nCnt=0;
function Tab0_tab4_Button3_OnClick(obj)
{
	var objDs = object(objCurGrid.bindDataset);
	if(nCnt ==0){
		create("File","tmpFile");
		nCnt++;
	}
	
	tmpFile.FileName = "c:/temp/"+objDs.id+".csv";
	tmpFile.Open("wt");
	
	if(objCurGrid==null)
	{
		alert("Grid를 선택하세요.");
		tmpFile.close();
		return;
	}
	
	tmpFile.write(objDs.saveCSV());
	tmpFile.close();
	alert(tmpFile.FileName+".csv 파일 저장이 완료되었습니다.");
}

