Window.Script:
function changeValue(ds_obj)
{
	this.Reload();
	
	Shockwave0.SetVariable("index_Name"	,ds_obj.getColumn(ds_obj.row, "index_Name")	);
	Shockwave0.SetVariable("value_Index",ds_obj.getColumn(ds_obj.row, "value_Index"));
	Shockwave0.SetVariable("L_m"		,ds_obj.getColumn(ds_obj.row, "L_m")		);
	Shockwave0.SetVariable("R_m"		,ds_obj.getColumn(ds_obj.row, "R_m")		);
	Shockwave0.SetVariable("bar_val"	,ds_obj.getColumn(ds_obj.row, "bar_val")	);
	Shockwave0.SetVariable("line_val"	,ds_obj.getColumn(ds_obj.row, "line_val")	);
}


function Button0_OnClick(obj)
{
	Shockwave0.SetVariable("index_Name","arget,Result,Achievement");
	Shockwave0.SetVariable("value_Index","SEA,CCC,DDD,RRRR");
	Shockwave0.SetVariable("L_m","count");
	Shockwave0.SetVariable("R_m","Achv.(%)");
	Shockwave0.SetVariable("bar_val","9234-5231,300-4123,606-2212,7900-9231");
	Shockwave0.SetVariable("line_val","105,110,20,80");
}



