}

function Grid0_OnHeadClick(obj,nCell,nX,nY)
{
	if ( GetKeyState("Control") )
		strSort = G_SortEx(obj,object(obj.BindDataset),nCell,strSort);	
	else
		strSort = G_Sort(obj,object(obj.BindDataset),nCell);
//trace(strSort);			
}

function Tab0_tab4_Checkbox0_OnClick(obj,bCheckedFlag)
{
	if ( bCheckedFlag )
		DS_Group.GroupKey = "Fab,Sect,Tech";
	else
		DS_Group.GroupKey = "";
}

