function changeDayColor() {

	for(day=1;day<32;day++){
		//요일 =체크를 의해 날짜 생성
		var date="01";
		if(day<10){
			date = "0" + day;
		}else{
			date = day;
		}
		date = SubStr(calBasicDt.Value,0,6) + date;
		
		if(GetDay(DateTime(date)) == 0 ) {
			gdDailyRate.SetCellProp("body",(day + 1),"Color","Red"); 
		} else if(GetDay(DateTime(date)) == 6 ) {
			gdDailyRate.SetCellProp("body",(day + 1),"Color","Blue"); 
		}else{
			gdDailyRate.SetCellProp("body",(day + 1),"Color","Black"); 
		}
	}
}

