function changeMonth(obj){
		if(obj.Id == "btnNextMonth"){
			var nMonth = fnc_LastDay(AddMonth(calBasicDt.Value,1));
			if(SubStr(today(),0,6) < subStr(nMonth,0,6) )
				return;
			else if (SubStr(today(),0,6) == subStr(nMonth,0,6))
				calBasicDt.Value = AddDate(today(), -1);
			else
				calBasicDt.Value = nMonth;
				
		}else{
			calBasicDt.Value=fnc_LastDay(AddMonth(calBasicDt.Value,-1));
		}
}

