Window.Script:
function intro_form_OnLoadCompleted(obj)
{
	CyXChart3_Load();
	CyXChart3.Visible = true;

}

function CyXChart3_Load()
{
	var mon_cnt = ds_right.GetRowCount();

	CyXChart3.TopGap = "28";						//차트 안쪽 상단 여백
	CyXChart3.LeftGap = "10";						//차트 안쪽 왼쪽 여백
	//CyXChart3.RightGap = "30";					//차트 안쪽 오른쪽 여백
	CyXChart3.BottomGap = "25";					//차트 안쪽 하단 여백

	CyXChart3.ClearData(1);						//Clear all numeric values set with the YValue or Value properties
	CyXChart3.ClearData(3);
	CyXChart3.ClearData(7);						//Clear all numeric values set with the YValue or Value properties
	
	CyXChart3.Stacked = 0;

	//6.2 추가 사항
	CyXChart3.PointLabels = True;
	CyXChart3.PointLabelColor = "black";


	//Chart 외곽 영역 색상
	CyXChart3.BackColor = "#f2f2fe";	
	
	//Chart 내부 색상
	CyXChart3.InsideColor = "#ffffff";				
	

	//Grid 가로선 색상
	CyXChart3.AxisY.Grid.Color = "#C7C8D4";		
	//선 종류
	CyXChart3.AxisY.Grid.Style = "DashStyle_Dash";
    
	//X축 Fonbt
	CyXChart3.AxisX.Font.Name = "Arial";
	CyXChart3.AxisX.Font.Size = "7";

    //6.2 추가
    CyXChart3.AxisY.Gridlines = True;
    CyXChart3.AxisX.Gridlines = True;

	//Max Value 설정 - Primary
    CyXChart3.AxisY.Max = ds_right.Max("col1") * 1.1;
    CyXChart3.AxisY.Min = 0;	//Min 값 을 반드시 지정해야 함
	CyXChart3.AxisY.LabelsFormat.Decimals = 0;
	CyXChart3.AxisY.LabelsFormat.Format = "AxisFormat_Number";
	CyXChart3.AxisY.AutoScale = False;


	//Max Value 설정 - Secondary
    CyXChart3.AxisY2.Max = ds_right.Max("col3") * 1.2;
    CyXChart3.AxisY2.Min = 0;	//Min 값 을 반드시 지정해야 함
	CyXChart3.AxisY2.LabelsFormat.Decimals = 0;
	CyXChart3.AxisY2.LabelsFormat.Format = "AxisFormat_Number";
	CyXChart3.AxisY2.AutoScale = False;


	CyXChart3.MarkerShape = 2;
//	CyXChart3.Scrollable = 1;

	CyXChart3.Axis[2].Scroll = 0;
	CyXChart3.Axis[2].pixperunit = 30;


	//X축의 갯수 설정
	//가운데 숫자가 시리즈의 갯수를 나타냄, 마지막 값은 X축 갯수 의미
	CyXChart3.OpenData("COD_Values", 3, mon_cnt);
	CyXChart3.OpenData("COD_Colors", 3, mon_cnt);

	
	CyXChart3.series[0].color = "#fcd03b";
	CyXChart3.Series[0].YAxis = 0; 							//어떤 Y축을 사용할지를 나타탬
	CyXChart3.series[0].gallery = "Gallery_Bar";
	CyXChart3.Series[0].CylSides = 24;
	
	CyXChart3.series[1].color = "#7896d6";
	CyXChart3.Series[1].YAxis = 0; 							//어떤 Y축을 사용할지를 나타탬
	CyXChart3.series[1].gallery = "Gallery_Bar";
	CyXChart3.Series[1].CylSides = 24;
	
	CyXChart3.series[2].color = "#f28498";
	CyXChart3.Series[2].YAxis = 1; 							//어떤 Y축을 사용할지를 나타탬
	CyXChart3.series[2].gallery = "Gallery_Lines";

	CyXChart3.series[2].LineWidth = 2;
	
	//X축 값 설정  Axis[2]는 X축을 나타냄(label)
	for(var i=0 ; i < mon_cnt ; i++){
		CyXChart3.Axis[2].Label(i) = ds_right.getcolumn(i,"col0");
	}
	
	//실제 데이타를 이용해 그래프를 그려주는 부분 (RowCount / ColCount 등을 이용)
	var NULL_VAL = "1.0E+308";	// data=0 인경우 표시안함
	
	for (var i=0 ; i < mon_cnt ; i++)
	{
		CyXChart3.Value(0,i) = ds_right.GetColumn(i, "col1");
		CyXChart3.Value(1,i) = ds_right.GetColumn(i, "col2");
		CyXChart3.Value(2,i) = ds_right.GetColumn(i, "col3");
	}	


	CyXChart3.Series[0].PointLabels = false;	
	CyXChart3.Series[1].PointLabels = false;	

	//Legend 표시
	CyXChart3.Series[0].Legend = "Plan  .";
	CyXChart3.Series[1].Legend = "Perf.";
	CyXChart3.Series[2].Legend = "%";

	CyXChart3.SerLegBoxObj.visible = true;

/////////////////////////////////////////////////////////////
	CyXChart3.SerLegBoxObj.height = "10";
//	CyXChart0.SerLegBoxObj.Docked = "Docked_Top";

	CyXChart3.SerLegBoxObj.Docked = "1028"; 	//top:256, right:515"

	CyXChart3.SerLegBoxObj.top = "15";
	CyXChart3.SerLegBoxObj.left = "20";
	CyXChart3.SerLegBoxObj.width  = "220";
	CyXChart3.SerLegBoxObj.height = "22";
//////////////////////////////////////////////////////////////////

	//Chart 외곽 Gradient 설정
	var BK0Gradient = CreateObject("ChartFX.ClientServer.GradientBack.62");
		
	BK0Gradient.Type = "GradientType_BackwardDiagonal";
	BK0Gradient.Colors.Item(0) = "#FFFFFF";
	BK0Gradient.Colors.Item(1) = "#98BDE0";
	BK0Gradient.Position.Item(0) = 0;
	BK0Gradient.Position.Item(1) = 0.5;
	BK0Gradient.Position.Item(2) = 1;
	BK0Gradient.Factor.Item(0) = 0;
	BK0Gradient.Factor.Item(1) = 0.5;
	BK0Gradient.Factor.Item(2) = 1;
	
	CyXChart3.BackObject = BK0Gradient;


	//Chart 내부 Gradient 설정
	var BK1Gradient = CreateObject("ChartFX.ClientServer.GradientBack.62");
		
	BK1Gradient.Type = "GradientType_BackwardDiagonal";
	BK1Gradient.Colors.Item(0) = "#FFFFFF";
	BK1Gradient.Colors.Item(1) = "#BDD8F2";
	BK1Gradient.Position.Item(0) = 0;
	BK1Gradient.Position.Item(1) = 0.5;
	BK1Gradient.Position.Item(2) = 1;
	BK1Gradient.Factor.Item(0) = 0;
	BK1Gradient.Factor.Item(1) = 0.5;
	BK1Gradient.Factor.Item(2) = 1;
	
	CyXChart3.InsideBackObject = BK1Gradient;

	CyXChart3.CloseData("COD_Values");
	CyXChart3.CloseData("COD_Colors");	
}
